
public class Test3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			boolean cond = 2<3;
			System.out.println(cond);
			//While loop with if condition
			int loop=0;
			while(true){
				System.out.println("The loop is break if it is loop < 5");
				if(loop==5){
					break;
				}
				loop ++;
				System.out.println("Running");
			}
			int num=5;
			if(num<5){
				System.out.println("The condition is True");
			}else if(num>6){
				System.out.println("The condition is false");
			}else if(num!=5){
				System.out.println("The condition is num==5");
			}else{
				System.out.println("The condition is nothing");
			}
			
			
	}

}
